import React, { useState } from 'react';
import ErrorModal from '../UI/ErrorModal/ErrorModal';

import styles from './SearchForm.module.css';

const SearchForm = (props) => {
  const [albumId, setAlbumId] = useState('');
  const [isNotValidId, setIsNotValidId] = useState(false);

  const onSubmitAlbumIdHandler = (event) => {
    event.preventDefault();
    if (albumId.trim().length === 0) {
      setIsNotValidId(true);
      return;
    }
    if (!Number.isInteger(+albumId) || +albumId < 0 || +albumId > 100) {
      setIsNotValidId(true);
      return;
    }
    props.onLoadAlbum(albumId);
    setAlbumId('');
  };

  const onAlbumIdChangeHandler = (event) => {
    const value = event.target.value;
    setAlbumId(value);
  };

  const errorHandler = () => {
    setIsNotValidId(false);
  };

  return (
    <React.Fragment>
      {isNotValidId && (
        <ErrorModal
          title="Invalid Input"
          message="Please enter a valid album ID ( 0<ID<100)"
          onRemoveErrorModal={errorHandler}
        />
      )}
      <div className={styles['search-container']}>
        <form onSubmit={onSubmitAlbumIdHandler}>
          <div className={styles.innerform}>
            <div className={`${styles.inputfield} ${styles.firstwrap}`}>
              <div className={styles.svgwrap}>
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  viewBox="0 0 24 24"
                >
                  <path d="M15.5 14h-.79l-.28-.27C15.41 12.59 16 11.11 16 9.5 16 5.91 13.09 3 9.5 3S3 5.91 3 9.5 5.91 16 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z"></path>
                </svg>
              </div>
              <input
                id={styles.search}
                type="number"
                placeholder="Get Album Photos By Id"
                value={albumId}
                onChange={onAlbumIdChangeHandler}
              />
            </div>
            <div className={`${styles.inputfield} ${styles.secondwrap}`}>
              <button className={styles['btn-search']} type="submit">
                SEARCH
              </button>
            </div>
          </div>
        </form>
      </div>
    </React.Fragment>
  );
};

export default React.memo(SearchForm);
